export default (ctx, canvas, paddleProps) => {
  class Paddle {
    constructor(x,y) {
      this.x = x;
      this.y = y
      this.height = 200;
      this.radius = paddleProps.width;
      this.colors = ["red", "#FFA62B"];
    }
    move() {
      ctx.beginPath();
      ctx.arc(this.x, this.y, this.radius, Math.PI*2,false);
      ctx.fillStyle = this.broke ? "white" : this.colors[1];
      ctx.strokeStyle = this.broke ? "white" : "red";
      ctx.lineWidth = 1;
      ctx.fillStyle = this.broke ? "white" : this.colors[1];
      ctx.shadowBlur = 0;
      ctx.shadowColor = "blue";
      ctx.strokeRect(this.x, this.y, this.width, this.height);
      ctx.fill();
      ctx.beginPath();
      ctx.arc(this.x-20, this.y, this.radius, Math.PI*2,false);
      ctx.fillStyle = this.broke ? "white" : this.colors[1];
      ctx.strokeStyle = this.broke ? "white" : "red";
      ctx.lineWidth = 1;
      ctx.fillStyle = this.broke ? "white" : this.colors[1];
      ctx.shadowBlur = 0;
      ctx.shadowColor = "blue";
      ctx.strokeRect(this.x, this.y, this.width, this.height);
      ctx.fill();
      ctx.arc(this.x+20, this.y, this.radius, Math.PI*2,false);
      ctx.fillStyle = this.broke ? "white" : this.colors[1];
      ctx.strokeStyle = this.broke ? "white" : "red";
      ctx.lineWidth = 1;
      ctx.fillStyle = this.broke ? "white" : this.colors[1];
      ctx.shadowBlur = 0;
      ctx.shadowColor = "blue";
      ctx.strokeRect(this.x, this.y, this.width, this.height);
      ctx.fill();
      ctx.arc(this.x, this.y+20, this.radius, Math.PI*2,false);
      ctx.fillStyle = this.broke ? "white" : this.colors[1];
      ctx.strokeStyle = this.broke ? "white" : "red";
      ctx.lineWidth = 1;
      ctx.fillStyle = this.broke ? "white" : this.colors[1];
      ctx.shadowBlur = 0;
      ctx.shadowColor = "blue";
      ctx.strokeRect(this.x, this.y, this.width, this.height);
      ctx.fill();
      ctx.arc(this.x, this.y-20, this.radius, Math.PI*2,false);
      ctx.fillStyle = this.broke ? "red" : this.colors[1];
      ctx.strokeStyle = this.broke ? "white" : "red";
      ctx.lineWidth = 1;
      ctx.fillStyle = this.broke ? "white" : this.colors[1];
      ctx.shadowBlur = 0;
      ctx.shadowColor = "blue";
      ctx.strokeRect(this.x, this.y, this.width, this.height);
      ctx.fill();
    }
  }

  let paddle = new Paddle(paddleProps.x,paddleProps.y);
  paddle.move();
  if (paddleProps.x <= 0) {
    paddleProps.x = 0;
  } else if (paddleProps.x + paddleProps.width >= canvas.width) {
    paddleProps.x = canvas.width - paddleProps.width;
  }
};
