import { useState, useEffect } from "react";
import { Navbar, Nav, Container } from "react-bootstrap";
import logo from '../assets/img/logo.svg';
import scrollLogo from '../assets/img/scrollLogo.svg'
import nameOnlyLogo from '../assets/img/nameOnlyLogo.svg'
import navIcon1 from '../assets/img/nav-icon1.svg';
import navIcon2 from '../assets/img/nav-icon2.svg';
import navIcon3 from '../assets/img/nav-icon3.svg';
import { HashLink } from 'react-router-hash-link';
import {
  BrowserRouter as Router
} from "react-router-dom";
import '../App.css'
import { SegmentedNav } from "react-bootstrap-icons";

export const NavBar = () => {
const [nav,setNav]= useState(false)
  const [activeLink, setActiveLink] = useState('home');
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
       setNav(false)

      } else {
        setScrolled(false);
        setNav(false)
        document.getElementsByClassName('.collapse')[0].collapse()

      }
    }

    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, [])

  const onUpdateActiveLink = (value) => {
    setActiveLink(value);
  }

  return (
    <Router>
      <Navbar  expand="md" className={scrolled ? "scrolled" :nav ===true ? 'navOpen': ""}>
        <Container>
          <Navbar.Brand  href="/">



           <img src={nameOnlyLogo} alt="Logo" className="logo" ></img>







          </Navbar.Brand>
          <Navbar.Toggle  aria-controls="basic-navbar-nav">
            <span onClick={()=>{ nav ===false && scrolled ===false ?setNav(true): nav===true? setNav(false):console.log()}} className="navbar-toggler-icon"></span>
          </Navbar.Toggle>
          <Navbar.Collapse className={'collapse'} id="basic-navbar-nav">
            <Nav className="ms-auto">
              <Nav.Link href="#home" className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('home')}>Home</Nav.Link>
              <Nav.Link href="#skills" className={activeLink === 'skills' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('skills')}>Skills</Nav.Link>
              <Nav.Link href="#projects" className={activeLink === 'projects' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('projects')}>Projects</Nav.Link>
            </Nav>
            <span className="navbar-text">
              <div className="social-icon">
                <a href="#  "><img src={navIcon1} alt="" /></a>
                <a href="#"><img src={navIcon2} alt="" /></a>
                <a href="#"><img src={navIcon3} alt="" /></a>
              </div>
              <HashLink to='#connect'>
                <button className="vvd"><span>Download CV</span></button>
              </HashLink>
            </span>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </Router>
  )
}
