export default function WallCollision(
  ballObj,
  canvas,
  player,
  paddleProps,
  setLives
) {
  if (ballObj.y + ballObj.rad > canvas.height || ballObj.y - ballObj.rad > canvas.height) {
    ballObj.dy *= -1;
  }
  if (ballObj.y - ballObj.rad < 0) {
    ballObj.dy *= -1;
  }

  if (ballObj.x + ballObj.rad > canvas.width || ballObj.x - ballObj.rad < 0) {
    ballObj.dx *= -1;
  }
}
