import { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import headerImg from "../assets/img/header-img.svg";
import { ArrowRightCircle } from 'react-bootstrap-icons';

import TrackVisibility from 'react-on-screen';

export const Banner = () => {
    const [clicked, setClicked] = useState(false);
    const [loopNum, setLoopNum] = useState(0);
    const [isDeleting, setIsDeleting] = useState(false);
    const [text, setText] = useState('');
    const [delta, setDelta] = useState(300 - Math.random() * 100);
    const [index, setIndex] = useState(1);
    const toRotate = ["Web Developer", "Web Designer", "UI/UX Designer"];
    const period = 2000;

    useEffect(() => {
        let ticker = setInterval(() => {
            tick();
        }, delta);

        return () => { clearInterval(ticker) };
    }, [text])

    const tick = () => {
        let i = loopNum % toRotate.length;
        let fullText = toRotate[i];
        let updatedText = isDeleting ? fullText.substring(0, text.length - 1) : fullText.substring(0, text.length + 1);

        setText(updatedText);

        if (isDeleting) {
            setDelta(prevDelta => prevDelta / 2);
        }

        if (!isDeleting && updatedText === fullText) {
            setIsDeleting(true);
            setIndex(prevIndex => prevIndex - 1);
            setDelta(period);
        } else if (isDeleting && updatedText === '') {
            setIsDeleting(false);
            setLoopNum(loopNum + 1);
            setIndex(1);
            setDelta(500);
        } else {
            setIndex(prevIndex => prevIndex + 1);
        }
    }

    return (
        <>
            <div className='tint'></div>


                <Container className="banner" id="home" >
                    <Row className="align-items-center">
                        <Col  xs={10} sm={12} md={8} lg={7} xl={7}>
                        <div className='banner-container'>
                            <TrackVisibility>
                                {({ isVisible }) =>
                                    <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>

                                        <p className="greeting">Hi! I'm Joe,</p>

                                    </div>}
                            </TrackVisibility>
                        </div>
                        </Col>
                        <Col
                            xs={12} sm={{ span: 12, offset: 0 }} md={{ span: 4, offset: 0}} lg={{ span: 5, offset: 0}} xl={{ span: 5, offset: 0}}>

                            <img className={clicked == 1 ? "bannerFast" : clicked == 2 ? "bannerFaster" : clicked == 3 ? "bannerFastest" : "bannerSlow"}
                                onClick={() => { clicked === 3 ? setClicked(0) : setClicked((clicked + 1)) }} src={headerImg} alt="Header Img" />




                        </Col>
                    </Row>
                </Container>

        </>
    )
}
